                        SocketService.getparams(url, params).then(function (resp) {
                            if (resp.data.status == false) {
                                $scope.phydocument_date = '';
                                $modalInstance.close('closed');
                                Notify.alert(resp.data.message, 'warning')
                            }
                        });
